import React, { useEffect, useState } from 'react';
import Button from '../../../components/button';
import Footer from '../../../components/footer';
import Header from '../../../components/header/index'
import imgTheater from '../../../assets/images/cinema.png';
import '../home/style.css'
import Input from '../../../components/input';
import '../filmeComum/style.css';

function FilmeComum() {

    const [idFilme, setIdFilme] = useState(0);
    const [Filme, setFilme] = useState('');
    const [Filmes, setFilmes] = useState([]);
    const [genero, setGenero] = useState('');
    const [generos, setGeneros] = useState([]);

    useEffect(() => {
        listar();
        listarGeneros();
    }, []);

    const listarGeneros = () => {
        fetch('http://localhost:5000/api/Generos', {
            method: 'GET',
            headers: {
                authorization: 'Bearer ' + localStorage.getItem('token-filmes')
            }
        })
            .then(response => response.json())
            .then(dados => {
                setGeneros(dados);
            })
            .catch(err => console.error(err));
    }

    const listar = () => {
        fetch('http://localhost:5000/api/Filmes', {
            method: 'GET',
            headers: {
                authorization: 'Bearer ' + localStorage.getItem('token-filmes')
            }
        })
            .then(response => response.json())
            .then(dados => {
                setFilmes(dados);
            })
            .catch(err => console.error(err));
    }
    return (
        <div>
            <Header description={"Faça o login e acesse a Coletânea"} />
            <main>
                <div className="imgTitulo">
                    <h2>Filme</h2>
                    <img className="theater" src={imgTheater} alt="teste" />
                </div>
                <div className="ListGenero">
                    <h3>Lista de filmes</h3>
                    <div className="Tabela">
                        <table>
                            <tbody>
                                {
                                    Filmes.map((item: any) => {
                                        return (
                                            <tr key={item.idFilme}>
                                                <td>{item.idFilme}</td>
                                                <td className="nometd">{item.titulo}</td>
                                            </tr>
                                        )
                                    })
                                }
                            </tbody>
                        </table>
                    </div>
                    <form onSubmit={event => {
                        event.preventDefault();
                        ;
                    }}>
                        <div className="form">
                            <div className="btn">
                                <Button value="Listar filmes" />
                            </div>
                        </div>
                    </form>
                </div>
            </main>
            <Footer />
        </div>
    );
}
export default FilmeComum;
